    public function testDefinirImagem()
    {
        $imagemBase64 = 'data:image/jpeg;base64,';

        $imagem = new \Domain\Entity\Imagem();
        $resposta = Formulario::setImagem($imagem, $imagemBase64);

        print_r($imagem);
